+ if (username.isEmpty() || password.isEmpty()) {
+ Toast.makeText(this, "Please fill all fields", Toast.LENGTH_SHORT).show();
+ return;
+ }
+
+ binding.btnLogin.setEnabled(false);
+ binding.btnLogin.setText("Logging in...");
+
+ JsonObject loginData = new JsonObject();
+ loginData.addProperty("username", username);
+ loginData.addProperty("password", password);
+
+ apiClient.postRequest("login.php", loginData, new Callback() {
+ @Override
+ public void onFailure(Call call, Exception e) {
+ runOnUiThread(() -> {
+ binding.btnLogin.setEnabled(true);
+ binding.btnLogin.setText("Login");
+ Toast.makeText(LoginActivity.this, "Network error: " + e.getMessage(), Toast.LENGTH_SHORT).show();
+ });
+ }
+
+ @Override
+ public void onResponse(Call call, Response response) throws Exception {
+ String responseBody = response.body().string();
+ Gson gson = new Gson();
+ JsonObject result = gson.fromJson(responseBody, JsonObject.class);
+
+ runOnUiThread(() -> {
+ binding.btnLogin.setEnabled(true);
+ binding.btnLogin.setText("Login");
+
+ if (result.get("success").getAsBoolean()) {
+ // Save user session
+ JsonObject user = result.getAsJsonObject("user");
+ saveUserSession(user);
+
+ Toast.makeText(LoginActivity.this, "Login successful!", Toast.LENGTH_SHORT).show();
+
+ Intent intent = new Intent(LoginActivity.this, MainActivity.class);
+ startActivity(intent);
+ finish();
+ } else {
+ Toast.makeText(LoginActivity.this, result.get("message").getAsString(), Toast.LENGTH_SHORT).show();
+ }
+ });
+ }
+ });
+ }
+
+ private void saveUserSession(JsonObject user) {
+ SharedPreferences prefs = getSharedPreferences("CitiAlertsPrefs", MODE_PRIVATE);
+ SharedPreferences.Editor editor = prefs.edit();
+
+ editor.putBoolean("isLoggedIn", true);
+ editor.putInt("userId", user.get("id").getAsInt());
+ editor.putString("username", user.get("username").getAsString());
+ editor.putString("email", user.get("email").getAsString());
+ editor.putString("firstName", user.get("first_name").getAsString());
+ editor.putString("lastName", user.get("last_name").getAsString());
+ editor.putString("userType", user.get("user_type").getAsString());
+ editor.putBoolean("isVerified", user.get("is_verified").getAsBoolean());
+
+ if (!user.get("phone").isJsonNull()) {
+ editor.putString("phone", user.get("phone").getAsString());
+ }
+
+ editor.apply();
+ }
